import { useCallback, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

import Navbar from './components/navbar'
import MyclassroomItem from './components/MyclassroomItem'
import BorderLine from './components/BorderLine'
import ClassForm from './components/ClassForm';
import { getUserId } from '../../cookie_handler/user_cookie_handler'
import ClassroomList from './components/ClassroomList';
import { fetchClassroomList } from './service/classroom_list_service';
import { fetchUserInfo } from './service/user_service';

function ClassroomPage() {
    const classroomWsUrl = "ws://35.229.189.182/ws/classroom"
    const navigate = useNavigate()
    const [ws,setWs] = useState(new WebSocket(classroomWsUrl + "/?userId=" + getUserId() + "&applicationType=app"))
    const [isLoading, setIsLoading] = useState(true)
    const [classroomList, setClassroomList] = useState([])
    const [userInfo, setUserInfo] = useState({})

    const fetchClassroomListCallBack = useCallback( () => {
        fetchClassroomList(setIsLoading, setClassroomList)
    }, [])

    const fetchUserInfoCallBack = useCallback( () => {
        fetchUserInfo(setUserInfo)
    }, [])

    const listenWsCallBack = useCallback( () => {
        ws.onclose = () => {
            console.log("close ws")
        }

        ws.onopen = () => {
            console.log("open ws")
        }

        ws.onmessage = (event) => {
            const message = JSON.parse(event.data)
            switch(message.cmd) {
                case "open room":
                    const newList = classroomList.map((classroom) => {
                        console.log(classroom)
                        if(classroom.classroomId === message.classroomId) {
                            return {...classroom, status: 'online'}
                        }
                        return classroom
                    })
                    console.log(newList)
                    setClassroomList(newList)
                    break;
                default:
                    console.log("default")
            }
        }
    }, [ws])
    // const renewClassroomStatus = (classroomId) => {
    //     const newList = classroomList.map((classroom) => {
    //         if(classroom.classroomId === classroomId) {
    //             return {...classroom, status: 'online'}
    //         }
    //         return classroom
    //     })
    //     console.log(newList)
    //     setClassroomList(newList)
    // }

    useEffect(() => {
        if(getUserId() === undefined) {
            navigate('/')
        }

        fetchClassroomListCallBack()
        fetchUserInfoCallBack()
        listenWsCallBack()

        // ws.onclose = () => {
        //     console.log("close ws")
        // }

        // ws.onopen = () => {
        //     console.log("open ws")
        // }

        // ws.onmessage = (event) => {
        //     const message = JSON.parse(event.data)
        //     switch(message.cmd) {
        //         case "open room":
        //             renewClassroomStatus(message.classroomId)
        //             break;
        //         default:
        //             console.log("default")
        //     }
        // }
    }, [fetchClassroomListCallBack, navigate, fetchUserInfoCallBack, listenWsCallBack])

    return (
        <div className='base'>
            <Navbar/>
            <div className='content'>
                <div className='mediaArea'>
                    <ClassForm/>
                </div>
                <div className='listArea'>
                    <MyclassroomItem userInfo={userInfo}/>
                    <BorderLine/>
                    { !isLoading && <ClassroomList classroomList={classroomList}/> }
                    { isLoading && <p>loading...</p>}
                </div>
            </div>
        </div>
    )
}

export default ClassroomPage
