function ClassroomVideo(props) {
    navigator.mediaDevices.getUserMedia({audio: false, video: true})
    .then((stream) => {
        console.log("got stream")
        const videoSelect = document.querySelector('#myVideo')
        console.log(videoSelect)
        videoSelect.srcObject = stream
    })

    const closeClassroom = () => {
        props.closeClassroomCallBack()
    }

    return(
        <div style={ {display: 'flex', flex} }>
            <video autoPlay playsInline id="myVideo"></video>
            <button onClick={closeClassroom}>close classroom</button>
        </div>
    )
}

export default ClassroomVideo
