import { getClassroomId } from "../../../api_handler/classroom_api_handler";
import { getUserId } from "../../../cookie_handler/user_cookie_handler";

export async function openClassroom(classroomWs, classSetting) {
    classroomWs.send(
        JSON.stringify({
            cmd: "open room",
            senderId: getUserId(),
            applicationType: "web",
            classroomId: await getClassroomId(),
            message: JSON.stringify(classSetting)
        })
    )
}

export async function closeClassroom(classroomWs) {
    classroomWs.send(
        JSON.stringify({
            cmd: "close room",
            senderId: getUserId(),
            applicationType: "web",
            classroomId: await getClassroomId(),
            message: ""
        })
    )
}

export async function onRequireAccess(setOpenModal, setModalContant, message) {
    const userInfo = JSON.parse(message.message)

    setModalContant({
        title: userInfo.userNickname + "想要加入教室",
        body: "要允許其加入教室嗎？",
        confirmFunction: () => {},
        cancelFunction: () => {}
    })
    setOpenModal(true)
}

