import { useCallback, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

import Navbar from './components/navbar'
import MyclassroomItem from './components/MyclassroomItem'
import BorderLine from './components/BorderLine'
import { getUserId } from '../../cookie_handler/user_cookie_handler'
import ClassroomList from './components/ClassroomList';
import { fetchClassroomList } from './service/classroom_list_service';
import { fetchUserInfo } from './service/user_service';

function ClassroomPage() {
    const navigate = useNavigate()
    const [isLoading, setIsLoading] = useState(true)
    const [classroomList, setClassroomList] = useState([])
    const [userInfo, setUserInfo] = useState({})

    const fetchClassroomListCallBack = useCallback( () => {
        fetchClassroomList(setIsLoading, setClassroomList)
    }, [])

    const fetchUserInfoCallBack = useCallback( () => {
        fetchUserInfo(setUserInfo)
    }, [])

    useEffect(() => {
        if(getUserId() === undefined) {
            navigate('/')
        }

        fetchClassroomListCallBack()
        fetchUserInfoCallBack()
    }, [fetchClassroomListCallBack, navigate, fetchUserInfoCallBack])

    return (
        <div className='base'>
            <Navbar/>
            <div className='content'>
                <div className='mediaArea'>123</div>
                <div className='listArea'>
                    <MyclassroomItem/>
                    <BorderLine/>
                    { !isLoading && <ClassroomList classroomList={classroomList}/> }
                    { isLoading && <p>loading...</p>}
                </div>
            </div>
        </div>
    )
}

export default ClassroomPage
