import { user_api_error_code } from "./error_code";
import { refreshToken } from "./user_api_handler";
import { getUserRefreshToken, setUserTokens } from "../cookie_handler/user_cookie_handler";

const classroomApiBaseUrl = "https://tmentor.xyz/classroom"

export async function getClassroomList(userId, userToken) {
    const response = await fetch(classroomApiBaseUrl + '/info/classroomList?userId=' + userId, {
        headers: {
            authToken: userToken
        }
    });

    const responseBody = await response.json()

    if(responseBody.code === user_api_error_code.tokenExpired) {
        newTokens = await refreshToken(getUserRefreshToken())
    }
    if(responseBody.code !== "0000") {
        throw new Error('get classroom list fail!');
    }

    return responseBody.data;
}
