import { getClassroomList } from "../../../api_handler/classroom_api_handler";

export async function fetchClassroomList(setIsLoading, setClassroomList) {
    let classroomList

    try {
        classroomList = await getClassroomList()
    } catch(e) {
        alert(e)
    }

    setClassroomList(classroomList)
    setIsLoading(false)
    return classroomList
}

export async function changeClassroomState(originClassroomList, setClassroomList) {
    const newList = stateRef.classroomList.map((classroom) => {
        console.log(classroom)
        if(classroom.classroomId === message.classroomId) {
            console.log(message.classroomId)
            return {...classroom, status: 'online'}
        }
        return classroom
    })
    console.log(newList)
    setClassroomList(newList)
}
