import { getUserId } from "../../../cookie_handler/user_cookie_handler"
const classroomWsUrl = "ws://35.229.189.182/ws/classroom"
let classroomWs

export function startClassroomWs(setClassroomList, classroomList) {
    classroomWs = new WebSocket(classroomWsUrl + "/?userId=" + getUserId() + "&applicationType=app")
    classroomWs.onmessage = function(event) {
        const message = JSON.parse(event.data)
        switch(message.cmd) {
            case "open room":
              console.log(message.classroomId)
              break;
          }
    }

    classroomWs.onclose = (event) => {
        console.log("close ws")
    }
}

export function closeClassroomWs() {
    if(classroomWs !== undefined) {
        classroomWs.close()
    }
}
